mapper.insert:
INSERT INTO thf_project_camera( code
                              , project_code
                              , project_name
                              , camera_name
                              , camera_username
                              , camera_password
                              , camera_ip
                              , camera_ip_port
                              , camera_channel
                              , camera_bit_stream
                              , cvr_host_username
                              , cvr_host_password
                              , updater
                              , update_datetime
                              , remark)
VALUES ( #{code}
       , #{projectCode}
       , #{projectName}
       , #{cameraName}
       , #{cameraUsername}
       , #{cameraPassword}
       , #{cameraIp}
       , #{cameraIpPort}
       , #{cameraChannel}
       , #{cameraBitStream}
       , #{cvrHostUsername}
       , #{cvrHostPassword}
       , #{updater}
       , #{updateDatetime}
       , #{remark})

mapper.update:
UPDATE thf_project_camera
SET camera_name      = #{cameraName}
  ,camera_username   = #{cameraUsername}
  ,camera_password   = #{cameraPassword}
  ,camera_ip         = #{cameraIp}
  ,camera_ip_port    = #{cameraIpPort}
  ,camera_channel    = #{cameraChannel}
  ,camera_bit_stream = #{cameraBitStream}
  ,cvr_host_username = #{cvrHostUsername}
  ,cvr_host_password = #{cvrHostPassword}

WHERE code = #{code}
